INSERT INTO `pages` (`id`, `url`, `title`, `meta`, `bodyclass`, `content`, `layout`, `status`, `user_id`, `navigation_id`, `hotel_id`, `created_at`, `updated_at`, `deleted_at`)
VALUES
	(1,'home','{\"en\":\"Home\",\"id\":\"Beranda\"}','{\"en\":\"The Sintesa Jimbaran Resort\",\"id\":\"The Sintesa Jimbaran Resort | Indonesian meta description\"}','','[{\"order\":1,\"status\":1,\"type\":\"sliders.fullscreen\",\"config\":{\"short\":false},\"data\":[{\"order\":1,\"title\":{\"en\":\"Destination: Jimbaran\"},\"subtitle\":{\"en\":\"Jimbaran Bay is one of iconic beaches in the South of Bali. This place is where we can find the majestic sunset emerged all year along the bay.\"},\"button\":{\"text\":{\"en\":\"Discover Jimbaran\"},\"link\":{\"type\":\"page\",\"url\":\"destination\"}},\"banner\":{\"src\":\"page-banners\\/destination.jpg\",\"alt\":\"Some alt text\"}},{\"order\":2,\"title\":{\"en\":\"The Best Moment to Stay\"},\"subtitle\":{\"en\":\"Enjoy the finest attributes of contemporary Balinese and Javanese design with modern life style concept and ultimately anticipate the needs of all our guests.\"},\"button\":{\"text\":{\"en\":\"learn more\"},\"link\":{\"type\":\"page\",\"url\":\"accommodation\"}},\"banner\":{\"src\":\"slider\\/pillow.jpg\",\"alt\":\"Some alt text\"}},{\"order\":3,\"title\":{\"en\":\"The BARber\"},\"subtitle\":{\"en\":\"An authentic reminiscent full service bar and lounge built as an icon to the newly established \\u2018The Sintesa\\u2019 Jimbaran.\"},\"button\":{\"text\":{\"en\":\"Explore the BARber\"},\"link\":{\"type\":\"page\",\"url\":\"barber\"}},\"banner\":{\"src\":\"slider\\/slide-2.jpg\",\"alt\":\"Some alt text\"}},{\"order\":4,\"title\":{\"en\":\"The Wellness Center\"},\"subtitle\":{\"en\":\"Sintesa Wellness Center offers a complete range of therapies experiences, luxurious, organic &amp; authentic.\"},\"button\":{\"text\":{\"en\":\"Discover Sintesa Wellness SPA\"},\"link\":{\"type\":\"page\",\"url\":\"the-wellness-center\"}},\"banner\":{\"src\":\"slider\\/slide-3.jpg\",\"alt\":\"Some alt text\"}},{\"order\":5,\"title\":{\"en\":\"Accommodation\"},\"subtitle\":{\"en\":\"206 beautiful appointed guestrooms and suites are available in unique tiers of accommodation which include Superior Room, Deluxe Room, Family Rooms and our Suites Collection.\"},\"button\":{\"text\":{\"en\":\"Explore Our Amenities\"},\"link\":{\"type\":\"page\",\"url\":\"accommodation\"}},\"banner\":{\"src\":\"slider\\/accommodation.jpg\",\"alt\":\"Some alt text\"}},{\"order\":6,\"title\":{\"en\":\"Water Fountain Poolside\"},\"subtitle\":{\"en\":\"Experiencing daylight while swimming just beside Angin Angin Restaurant\"},\"button\":{\"text\":{\"en\":\"Learn More\"},\"link\":{\"type\":\"page\",\"url\":\"swimming-pools\"}},\"banner\":{\"src\":\"slider\\/fountain.jpg\",\"alt\":\"Some alt text\"}},{\"order\":7,\"title\":{\"en\":\"Sunken Lounge\"},\"subtitle\":{\"en\":\"<p>Perfect place to chill and hangout with the best pool view for you and your family.<br><\\/p>\"},\"button\":{\"text\":{\"en\":\"Explore\"},\"link\":{\"type\":\"page\",\"url\":\"sunken-lounge\"}},\"banner\":{\"src\":\"slider\\/sunken.jpg\",\"alt\":\"Some alt text\"}},{\"order\":8,\"title\":{\"en\":\"Lobby Lounge\"},\"subtitle\":{\"en\":\"<p>With mix elements of Indonesian culture, feel the cultural ambience.<\\/p>\"},\"banner\":{\"src\":\"slider\\/lobby-lounge.jpg\",\"alt\":\"Some alt text\"},\"button\":{\"link\":{\"type\":\"\"}}}]},{\"order\":2,\"status\":1,\"type\":\"custom.booking_bar\"},{\"order\":3,\"status\":1,\"type\":\"text.title_subtitle\",\"config\":{\"pattern\":true},\"data\":{\"title\":{\"en\":\"\'The Sintesa\' Jimbaran - Bali\"},\"subtitle\":{\"en\":\"Sintesa Hotels Group proudly to present the opening of \\u2018The Sintesa \\u2018 Jimbaran - Bali, a new concept of upscale hotel in Jimbaran and located on Bali\\u2019s southwestern coast is connecting between Jimbaran Bay and Clift (Bukit) of Jimbaran. The Sintesa Jimbaran Bali, designed for natural living with choices of the wellness and lifestyle activities and facilities to deliver \\u2018a best moment to stay\\u2018.\"},\"text\":{\"en\":\"<p>The Sintesa Jimbaran \\u2013 Bali is just 10 km south of the Bali Airport (Denpasar). \'The Sintesa\' Jimbaran Bali is a short walk to many of Bali\'s most famous traditional beach - side Seafood Restaurants and is a close drive by hotel shuttle  to Caf\\u00e9s and Bars at Jimbaran Bay and Uluwatu. The Jimbaran region is also home to several world-class hotels and high end villas. Jimbaran is where we enjoy the majestic sunset emerged all year along the bay. Hotel\\u2019s location is conveniently positioned, is only 15 km away from vibrant area of Kuta and Nusa Dua. The Hotel is easily accessible to the main tourist quarter of Legian and Seminyak Beach with its world- famous shopping and nightlife venues.<\\/p>\"}}},{\"order\":4,\"status\":1,\"type\":\"parallax.image_text\",\"data\":{\"title\":{\"en\":\"Destination: Jimbaran\"},\"description\":{\"en\":\"Jimbaran Bay is one of iconic beaches in the South of Bali. This place is where we can find the majestic sunset emerged all year along the bay. Brightly painted traditional Jukung fishing boats dot the white sand in the early morning, the fishermen are coming into shore with last night\\u2019s catch.\"},\"background\":{\"src\":\"generic\\/jimbaran.jpg\",\"alt\":\"some text\"},\"button\":{\"text\":{\"en\":\"Explore Jimbaran\"},\"link\":{\"type\":\"page\",\"url\":\"destination\"}}}},{\"order\":5,\"status\":1,\"type\":\"text_image.two_alt\",\"data\":[{\"title\":{\"en\":\"The BARber\"},\"description\":{\"en\":\"An authentic reminiscent full service bar and lounge built as an icon to the newly established \\u2018The Sintesa\\u2019 Jimbaran.\"},\"background\":{\"src\":\"generic\\/home-barber.jpg\",\"alt\":\"Some text\"},\"link\":{\"type\":\"page\",\"url\":\"barber\"}},{\"title\":{\"en\":\"The Wellness Center\"},\"description\":{\"en\":\"Sintesa Wellness SPA offers a complete range of therapies experiences, luxurious, organic & authentic.\"},\"background\":{\"src\":\"generic\\/home-wellness.jpg\",\"alt\":\"Some text\"},\"link\":{\"type\":\"page\",\"url\":\"the-wellness-center